/* FUNCTIONS*/


initPage = function() {
  var key, value;
  for (key in playerChannels) {
    value = playerChannels[key];
    addPlayer(value, key);
  }
  return setupLayout('threeUp');
};

initEvents = function() {
  $('.alert').bind('closed.bs.alert', function() {
    return setTimeout(doResize, 100);
  });
  setTimeout(updateTotal, 5000);
  $("a[href='#info-content']").click(function() {
    $('#content-nav li a.active').removeClass('active');
    $("#content-nav li a[href='#info-content']").addClass('active');
    $('#stream-index').hide(0);
    return $('#info-content').show(0, function() {
      return $('html, body').animate({
        scrollTop: $("#content-nav").offset().top
      }, 600);
    });
  });
  return $("a[href='#stream-index']").click(function() {
    $('#content-nav li a.active').removeClass('active');
    $("#content-nav li a[href='#stream-index']").addClass('active');
    $('#info-content').hide(0);
    return $('#stream-index').show(0, function() {
      return $('html, body').animate({
        scrollTop: $("#content-nav").offset().top
      }, 600);
    });
  });
};

initIndex = function() {
  var context, html, source, template;
  source = $('#index-template').html();
  template = Handlebars.compile(source);
  context = {
    users: users
  };
  html = template(context);
  $('#stream-container').append(html);
  $('.index-entry:nth-child(4n+5)').css("clear", "both");
  return $('.index-entry').each(function() {
    return refreshStream($(this).attr('data-channel'));
  });
};

refreshStream = function(channel) {
  return $.ajax({
    type: 'GET',
    dataType: 'jsonp',
    crossDomain: true,
    jsonp: 'callback',
    url: STREAM_API_URL + channel,
    success: function(data) {
      var channelEntry, stream;
      stream = data["stream"];
      channelEntry = $("div[data-channel='" + channel + "']");
      if (stream === null) {
        channelEntry.find('p.live').removeClass('on-air');
        channelEntry.find('.stream-pic').removeClass('on-air');
        channelEntry.find('h2').text('Off-Air');
        return channelEntry.find('.game-title').text('nothing at the moment');
      } else {
        channelEntry.find('p.live').addClass('on-air');
        channelEntry.find('.stream-pic').addClass('on-air');
        channelEntry.find('h2').text(stream["channel"]["status"]);
        channelEntry.find('.stream-pic').attr('src', stream["preview"]["large"]);
        return channelEntry.find('.game-title').text(stream["channel"]["game"]);
      }
    },
    complete: setTimeout((function() {
      return refreshStream(channel);
    }), STREAM_UPDATE_RATE)
  });
};

doResize = function() {
  return setupLayout(currentLayout);
};

setCoords = function() {
  var desiredHeight, height, scale, top, winHeight;
  if ($('#chat1').is(":visible")) {
    CHAT_WIDTH = 300;
  } else {
    CHAT_WIDTH = 0;
  }
  WIDTH_LARGE = $('#streams-wrapper').width() - CHAT_WIDTH;
  HEIGHT_LARGE = (WIDTH_LARGE * 9 / 16) + 28;
  WIDTH_MED = $('#streams-wrapper').width() / 2;
  HEIGHT_MED = (WIDTH_MED * 9 / 16) + 28;
  WIDTH_SMALL = WIDTH_LARGE / 2;
  HEIGHT_SMALL = (WIDTH_SMALL * 9 / 16) + 28;
  if (currentLayout === "threeUpHorizontal") {
    WIDTH_SMALL = 300;
    HEIGHT_SMALL = 200;
    WIDTH_LARGE = $('#streams-wrapper').width() - WIDTH_SMALL;
    HEIGHT_LARGE = (WIDTH_LARGE * 9 / 16) + 28;
  }
  height = 0;
  if (currentLayout === "threeUp") {
    height = HEIGHT_LARGE + HEIGHT_SMALL;
  } else if (currentLayout === "threeUpHorizontal") {
    height = HEIGHT_LARGE;
  } else if (currentLayout === "oneUp") {
    height = HEIGHT_LARGE;
  } else {
    height = HEIGHT_MED * 2;
  }
  top = $('#streams-wrapper').position().top;
  winHeight = $(window).height();
  if ((top + height) > winHeight) {
    desiredHeight = winHeight - top;
    scale = desiredHeight / height;
    HEIGHT_LARGE *= scale;
    WIDTH_LARGE = (HEIGHT_LARGE - 28) * 16 / 9;
    WIDTH_MED = (HEIGHT_MED - 28) * 16 / 9;
    HEIGHT_MED *= scale;
    WIDTH_SMALL = WIDTH_LARGE / 2;
    HEIGHT_SMALL = (WIDTH_SMALL * 9 / 16) + 28;
    CHAT_WIDTH = $('#streams-wrapper').width() - WIDTH_LARGE;
  }
  return layouts = {
    grid: {
      stream1: {
        x: 0,
        y: 0,
        width: WIDTH_MED,
        height: HEIGHT_MED
      },
      stream2: {
        x: WIDTH_MED,
        y: 0,
        width: WIDTH_MED,
        height: HEIGHT_MED
      },
      stream3: {
        x: 0,
        y: HEIGHT_MED,
        width: WIDTH_MED,
        height: HEIGHT_MED
      },
      chat1: {
        x: WIDTH_MED,
        y: HEIGHT_MED + CHAT_TAB_HEIGHT,
        width: WIDTH_MED,
        height: HEIGHT_MED - CHAT_TAB_HEIGHT
      },
      chatnav: {
        x: WIDTH_MED,
        y: HEIGHT_MED,
        width: WIDTH_MED,
        height: CHAT_TAB_HEIGHT
      },
      overallHeight: HEIGHT_MED * 2
    },
    oneUp: {
      stream1: {
        x: 0,
        y: 0,
        width: WIDTH_LARGE,
        height: HEIGHT_LARGE
      },
      chat1: {
        x: WIDTH_LARGE,
        y: CHAT_TAB_HEIGHT,
        width: CHAT_WIDTH,
        height: HEIGHT_LARGE - CHAT_TAB_HEIGHT
      },
      chatnav: {
        x: WIDTH_LARGE,
        y: 0,
        width: CHAT_WIDTH,
        height: CHAT_TAB_HEIGHT
      },
      overallHeight: HEIGHT_LARGE
    },
    threeUp: {
      stream1: {
        x: 0,
        y: 0,
        width: WIDTH_LARGE,
        height: HEIGHT_LARGE
      },
      stream2: {
        x: 0,
        y: HEIGHT_LARGE,
        width: WIDTH_SMALL,
        height: HEIGHT_SMALL
      },
      stream3: {
        x: WIDTH_SMALL,
        y: HEIGHT_LARGE,
        width: WIDTH_SMALL,
        height: HEIGHT_SMALL
      },
      chat1: {
        x: WIDTH_LARGE,
        y: CHAT_TAB_HEIGHT,
        width: CHAT_WIDTH,
        height: HEIGHT_LARGE + HEIGHT_SMALL - CHAT_TAB_HEIGHT
      },
      chatnav: {
        x: WIDTH_LARGE,
        y: 0,
        width: CHAT_WIDTH,
        height: CHAT_TAB_HEIGHT
      },
      overallHeight: HEIGHT_LARGE + HEIGHT_SMALL
    }
  };
};

setupLayout = function(layoutType) {
  currentLayout = layoutType;
  setCoords();
  $('div.layoutElement').each(function(index) {
    return layoutElement($(this), layouts[layoutType][$(this).attr('id')]);
  });
  layoutPlayerSlot('stream1');
  layoutPlayerSlot('stream2');
  layoutPlayerSlot('stream3');
  layoutChat('chat1');
  layoutChatNav('chatnav');
  return $('#streams-wrapper').height(layouts[currentLayout].overallHeight);
};

layoutChat = function(chat) {
  var layout;
  layout = layouts[currentLayout][chat];
  chat = $('#' + chat);
  if (layout) {
    chat.css('left', layout.x);
    chat.css('top', layout.y);
    chat.width(layout.width);
    return chat.height(layout.height);
  }
};

layoutChatNav = function(chatnav) {
  var layout;
  layout = layouts[currentLayout][chatnav];
  chatnav = $('#' + chatnav);
  if (layout) {
    chatnav.css('left', layout.x);
    chatnav.css('top', layout.y);
    chatnav.width(layout.width);
    return chatnav.height(layout.height);
  }
};

toggleChat = function() {
  $('#chat1').toggle();
  return layout(currentLayout);
};

layoutPlayerSlot = function(slot) {
  var layout, player;
  layout = layouts[currentLayout][slot];
  player = $('#' + slot);
  if (layout) {
    if (player.is('div')) {
      addPlayer(playerChannels[slot], slot);
      player = $('#' + slot);
    }
    player.css('left', layout.x);
    player.css('top', layout.y);
    player.width(layout.width);
    return player.height(layout.height);
  } else {
    return removePlayer(player);
  }
};

layoutElement = function(element, layout) {
  if (layout) {
    element.css('left', layout.x);
    element.css('top', layout.y);
    element.width(layout.width);
    element.height(layout.height);
    return element.show();
  } else {
    return element.hide();
  }
};

embedPlayer = function(channel, replaceElem) {
  return swfobject.embedSWF(PLAYER_URL + "?channel=" + channel, replaceElem, "100", "100", "9.0.0", "expressInstall.swf", flashvars, params, attributes);
};

addPlayer = function(channelName, slotName) {
  embedPlayer(channelName, slotName);
  return $('#' + slotName).attr('class', 'layoutElement');
};

removePlayer = function(playerElement) {
  var div, name;
  name = playerElement.attr('id');
  div = "<div id='" + name + "'></div>";
  return playerElement.replaceWith(div);
};

updateTotal = function() {
  return $.ajax({
    type: 'GET',
    url: 'http://www.extra-life.org/index.cfm?fuseaction=widgets.300x250thermo&teamID=10592',
    success: function(data) {
      var value;
      value = $(data.responseText).find('#mercury em').text();
      return $('#total-value').text(value);
    },
    complete: function() {
      return setTimeout(updateTotal, TOTAL_UPDATE_RATE);
    }
  });
};

swapStream = function(slot, channel) {
  playerChannels[slot] = channel;
  removePlayer($('#' + slot));
  return setupLayout(currentLayout);
};

loadChat = function(channel) {
  var channelKey;
  channelKey = channel;
  if (channel !== 'irc') {
    channel = playerChannels[channel];
  }
  if ((channel !== currentChat) && $('#chat1').is(':visible')) {
    document.getElementById('chat1').src = getChatUrl(channel);
    currentChat = channel;
    $('#chatnav ul li a.active').removeClass('active');
    return $("#chatnav ul li a[data-chat='" + channelKey + "']").addClass('active');
  }
};

getChatUrl = function(channel) {
  var url;
  url = '';
  if (channel === 'irc') {
    url = IRC_URL;
  } else {
    url = 'http://www.twitch.tv/chat/embed?channel=' + channel + '&popout_chat=true';
  }
  return url;
};

/* MAIN SCRIPT*/


$(document).ready(function() {
  initPage();
  initEvents();
  return initIndex();
});

$(window).resize(function() {
  return doResize();
});
